/* importamos img    */

  import logo from './img/logotipo.png';
  import './css/estilos.css';
  import 'bootstrap/dist/css/bootstrap.min.css';
  import { BrowserRouter , Routes,Route, Navigate} from 'react-router-dom';  
  import { useState, useContext  } from 'react';

  /* importamos componentes */

  import NavBar from './components/NavBar/NavBar';

  import ItemListContainer from './components/ItemListContainer/ItemListContainer';

  import ItemDetailContainer from './components/ItemDetailContainer/ItemDetailContainer';

  import FormLogin from './components/User/Login/Form';

  import Cart from './components/Cart/Cart'    

  import Error404 from './components/Error404/error404';

  import { CartProvider } from "./context/cartContext";
  import { LoginProvider} from "./context/loginContext";   
import userEvent from '@testing-library/user-event';


function App() {


            return (

            <div className='inicio' >

                        <LoginProvider>
                              <CartProvider>
                                    <BrowserRouter>
                                    {
                                          user.logged ?
                                          <>
                                    <NavBar />
                                    <Routes>
                                                <Route  path='/category/:categoryid' element={<ItemListContainer greeting="M치s vendidos en Rines de Autos y Camionetas" />} />
                                                <Route  path='/item/:id' element={<ItemDetailContainer  />} />
                                                <Route  path='/cart' element={<Cart/>} />
                                                <Route  path='/user/login' element={<FormLogin/>} />
                                                <Route  path='/'  element={<ItemListContainer greeting="M치s vendidos en Rines de Autos y Camionetas" />} />
                                                <Route  path='*' element={ <Error404/>} />
                                                <Route  path='/category/:categoryid' element={<ItemListContainer greeting="M치s vendidos en Rines de Autos y Camionetas" />} />

                                    </Routes>
                                          </>
                                          :
                                          <Routes>
                                          <Route  path='/category/:categoryid' element={<ItemListContainer greeting="M치s vendidos en Rines de Autos y Camionetas" />} />

                                          </Routes>

                                    }      







                                    </BrowserRouter>   
                              </CartProvider>       
                        </LoginProvider>   


            </div>           

            );



}

export default App;
