import './itemlistcontainer.css';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import logo from '../../img/media/rn1.jpg';

import { useState , useEffect} from 'react';    

import Productos from './data.json';
import { Link, useParams } from 'react-router-dom';


const ItemListContainer = ({greeting}) =>
{
        const parametros = useParams();

        const categoryid = parametros.categoryid;

        const [productos, setProductos] = useState([]);

        const pedirDatos = () =>
        {
                        return new Promise( (resuelta,rechazada)=>
                        {
                                setTimeout(() =>
                                {
                                        resuelta(Productos);            
                                },1000)
                        })
        };


       
        useEffect(() => {
                        pedirDatos()
                                .then((result) => {

                                        if(categoryid)
                                        {
                                                setProductos(result.filter(result => result.category == categoryid ))
                                        }
                                        else
                                        {
                                                setProductos(result)
                                        }
                                                
                                }).catch((err) => {
                                                console.log(err);
                                });
        },[]);



            return(
                 <div className="itemlist ">
                        
                        <h3 className="container">{greeting}</h3>
                                <div className='row col-md-12 productos'>
                                        
                                        {
                                                        productos.map((data=>
                                                        (
                                                                <div className="col-lg-3 col-md-6 mb-4" key={data.id}>
                                                                        <Card  className="tarjetas" >
                                                                                <Card.Img variant="top"  src={require(`../../img/media/${data.img}`)}></Card.Img>
                                                                                <Card.Body>
                                                                                <Card.Title className='text-center'>{data.name} {data.model}</Card.Title>
                                                                                <h4  className='text-center text-primary'><b>$ {data.price}</b></h4>
                                                                                <Card.Text className='text-center'>Dimensiones:  {data.size} </Card.Text>
                                                                                </Card.Body>
                                                                                <Card.Footer className='d-grid gap-2'>
                                                                                                <Link className="btn btn-primary btn-"  to={`../item/${data.id}`}  size="md" > Ver detalles</Link>
                                                                                </Card.Footer>
                                                                        </Card>
                                                                </div> 
                                                        )))
                                                        
                                        }
                                                
                
                                </div>     
                 </div>   
                
                );
}

 export default ItemListContainer;
