import './itemlistcontainer.css';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import logo from '../../img/media/rn1.jpg';

import { useState } from 'react';    

import Productos from './data.json';


const ItemListContainer = ({greeting}) =>
{

        const [productos, setProductos] = useState([]);

        const pedirDatos = () =>
        {
                        return new Promise( (resuelta,rechazada)=>
                        {
                                setTimeout(() =>
                                {
                                        resuelta(Productos);            
                                },1000)
                        })
        };


                pedirDatos()
                .then((result) => {
                        setProductos(result)
                }).catch((err) => {
                        console.log(err);
                });


            return(
                 <div className="itemlist ">
                        <h3 className="container">{greeting}</h3>
                       <div className='row col-md-12 productos'>
                            <div className="col-lg-3 col-md-6 mb-4">
                            <Card  style={{ width: '18rem' }} >
                                    <Card.Img variant="top" src={logo} />
                                    <Card.Body>
                                    <Card.Title>Rin 18 5/100 </Card.Title>
                                    <Card.Text>
                                            Motor Sports - Modelo 5503
                                    </Card.Text>
                                    </Card.Body>
                                    <Card.Footer>
                                    <small className="text-muted">$10,000</small>
                                    <Button className="btn btn-primary"  > Ver detalles</Button>
                                    </Card.Footer>
                            </Card>
                                    
                            </div> 
                          
                            
                        </div>     
                 </div>   
                
                );
}

 export default ItemListContainer;
