import './itemlistcontainer.css';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import logo from '../../img/media/rn1.jpg';

import { useState , useEffect} from 'react';    

import Productos from './data.json';


const ItemListContainer = ({greeting}) =>
{

        const [productos, setProductos] = useState([]);

        const pedirDatos = () =>
        {
                        return new Promise( (resuelta,rechazada)=>
                        {
                                setTimeout(() =>
                                {
                                        resuelta(Productos);            
                                },1000)
                        })
        };


       
        useEffect(() => {
                        pedirDatos()
                                .then((result) => {
                                                setProductos(result)
                                }).catch((err) => {
                                                console.log(err);
                                });
        },[]);

                console.log(productos)

            return(
                 <div className="itemlist ">
                        <h3 className="container">{greeting}</h3>
                       <div className='row col-md-12 productos'>
                            
                            {
                                        productos.map((data=>
                                        (
                                                <div className="col-lg-3 col-md-6 mb-4">
                                                 <Card  style={{ width: '18rem' }} key={data.id} >
                                                        <Card.Img variant="top" src={logo} />
                                                        <Card.Body>
                                                        <Card.Title>{data.name} {data.model}</Card.Title>
                                                        <Card.Text>Dimensiones:  {data.size} </Card.Text>
                                                        </Card.Body>
                                                        <Card.Footer>
                                                        <div className='row col-md-12' >
                                                                <div className='col-md-5'>
                                                                        <h3 className="text-muted "><b>$ {data.price}</b></h3>
                                                                </div>
                                                                <div className='col-md-7'>
                                                                        <Button className="btn btn-primary"  > Ver detalles</Button>
                                                                </div>
                                                        </div>
                                                        </Card.Footer>
                                                </Card>
                                                </div> 
                                        )))
                                        
                           }
                                 
   
                        </div>     
                 </div>   
                
                );
}

 export default ItemListContainer;
