import './cart.css';
import Table from 'react-bootstrap/Table';


import { useContext } from 'react'; 

import { CartContext } from "../../context/cartContext";



const Cart = ()=>
{

        //valores globales de context para el cart section            
        const { cart,addCarrito,isInCart } = useContext(CartContext);

          console.log(cart)  

    return(
        <div className='content' >
              <div className="item card " >
              <h1>CARRITO DE COMPRAS</h1>
                <div className='container mt-4 '>
                    <div className='row col-md-12'>
                    {
                            cart.map(data => (

                                <div className='row col-md-12 ' key={data.id}>
                                     <hr></hr>
            
                                        <div className='col-md-2'>
            
                                                <img  className='img-principal'  src={require(`../../img/media/${data.img}`)} ></img>
                                        </div>
            
                                        <div className='col-md-8' >
                                            <h4>{data.name} {data.model}  </h4>
                                            <h4 className='text-primary'><b>{new Intl.NumberFormat('en-US', {style: 'currency', currency: 'USD'}).format(data.price)}</b></h4>
                                                    
                                                
                                        </div>     
                                        <div className='col-md-2'>
                                        <select class="form-control form-control-sm ng-valid ng-not-empty ng-dirty ng-valid-parse ng-touched" ng-model="item.quantity" ng-options="quantity for quantity in [] | range:1:item.maxItemsToSell track by quantity" ng-change="cart.updateQuantity(item.sku, item.quantity)" style=""><option label="1" value="1" selected="selected">1</option><option label="2" value="2" selected="selected">2</option><option label="3" value="3" selected="selected">3</option><option label="4" value="4">4</option><option label="5" value="5">5</option><option label="6" value="6">6</option><option label="7" value="7">7</option><option label="8" value="8">8</option></select>
                                        </div> 

                                    
            
                            </div>

                            ))
                    }

                    </div>
                </div> 
              </div>
        </div>
    )
}

export default Cart;
