import './cart.css';
import Table from 'react-bootstrap/Table';


import { useContext } from 'react'; 

import { CartContext } from "../../context/cartContext";



const Cart = ()=>
{

        //valores globales de context para el cart section            
        const { cart,addCarrito,isInCart } = useContext(CartContext);

          console.log(cart)  

    return(
        <div className='content' >
              <div className="item card " >
              <h1>CARRITO DE COMPRAS</h1>
                <div className='container mt-4 '>
                    <div className='row col-md-12'>
                    {
                            cart.map(data => (

                                <div className='row col-md-12 ' key={data.id}>
                                     <hr></hr>
            
                                        <div className='col-md-2'>
            
                                                <img  className='img-principal'  src={require(`../../img/media/${data.img}`)} ></img>
                                        </div>
            
                                        <div className='col-md-8' >
                                            <h4>{data.name} {data.model}  </h4>
                                            <h4 className='text-primary'><b>{new Intl.NumberFormat('en-US', {style: 'currency', currency: 'USD'}).format(data.price)}</b></h4>
                                                    
                                                
                                        </div>     
                                        <div className='col-md-2'>
                                            <label>Cantidad</label>
                                                <input value={data.cantidad} type="disbled" className='form-control'></input>
                                        </div> 

                                    
            
                            </div>

                            ))
                    }

                    </div>
                </div> 
              </div>
        </div>
    )
}

export default Cart;
