import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';

import './ItemDetailContainer.css';

import Items from '../ItemListContainer/data.json';
import { Link, useParams } from 'react-router-dom';
import { useState , useEffect} from 'react'; 



        const ItemDetailContainer = () =>
        {


            const {id} = useParams();

            const [item, setitems] = useState([]);


            const pedirDatos = () =>
            {
                            return new Promise( (resuelta)=>
                            {
                                            resuelta(Items);            
                                    
                            })
            };

            useEffect(() => {
                pedirDatos()
                        .then((result) => {

                                if(id)
                                {
                                    setitems(result.filter(item  => item.id == id));
                                    
                                }
                                        
                        }).catch((err) => {
                                        console.log(err);
                        });
            },[id]);

                        console.log(Items)
            return(
                <div className='content'>
                       
                                       {
                                                       item.map((data=>
                                                       (
                                                        
                                                        <div className="item card " >
                                                            <div className='row col-md-12'>
                                        
                                                                    <div className='col-md-5'>
                                        
                                                                            <img  className='img-principal'  src={require(`../../img/media/${data.img}`)} ></img>

                                                                      
                                                                    </div>
                                        
                                                                    <div className='col-md-7'>

                                        
                                                                            <h1 className='text-center'>{data.name} {data.model} <span  className=' text-primary'><b>$ {data.price}</b></span> </h1>
                                                                            <div className="product-bg-container product-general-info variation-info ">
                                                                        <div className="row">
                                                                                </div>
                                                                        <div className="row is-fixed" data-sticky-button="">
                                                                                <div className="product-quantity-container col-3">
                                                                                <Form.Select aria-label="Default select example">
                                                                                
                                                                                <option value="1">1</option>
                                                                                <option value="2">2</option>
                                                                                <option value="3">3</option>
                                                                                </Form.Select>
                                                                                        
                                                                        </div>
                                                                                        <div className="col-9">
                                                                                <button id="buy-now" className="btn btn-lg buy-now-button btn-primary">Añadir al carrito</button>
                                                                        </div>
                                                                        </div>
                                                                        </div>
                                                                               
                                                                                <Card.Text classNameName='text-left'><h4><span classNameName='text-primary'>Modelo</span> <br></br> {data.model}</h4> </Card.Text>   
                                                                                <Card.Text classNameName='text-left'><h4><span classNameName='text-primary'>Descripción</span><br></br>  {data.description} </h4> </Card.Text>
                                                                                <Card.Text className='text-left'><h4><span className='text-primary'>Dimensiones </span><br></br> {data.size}</h4> </Card.Text>   
                                                                        </div>
                                        
                                                        </div>                        
                                        
                                                        </div>
                                        
                                                       )))
                                                       
                                       }
                                               
               </div>
               
               );
        
}

 export default ItemDetailContainer;
