import Card from 'react-bootstrap/Card';
import './ItemDetailContainer.css';
import Productos from './data.json';



        const ItemDetailContainer = () =>
        {


            const {item} = useParams();

            const [productos, setProductos] = useState([]);
    
            const pedirDatos = () =>
            {
                            return new Promise( (resuelta,rechazada)=>
                            {
                                            resuelta(Productos);            
                                    
                            })
            };
           
    
    
            useEffect(() => {
                            pedirDatos()
                                    .then((result) => {
    
                                            if(categoryid)
                                            {
                                                    setProductos(result.filter(productos  => productos.category == categoryid));
                                                
                                            }
                                            else
                                            {
                                                    setProductos(result);
                                            }
    
                                                    
                                    }).catch((err) => {
                                                    console.log(err);
                                    });
            },[categoryid]);


            const data = {
                "name":"Rines PHW",
                "model":"2350",
                "size":"12x100",
            }

            return(
            <div className='content'>
                <div className="item card " >
                    <div className='row col-md-12'>

                            <div className='col-md-6'>

                                    <img  className='img-principal'  src={require(`../../img/media/1.png`)} ></img>

                            </div>

                            <div className='col-md-6'>

                                    <h1 className='text-center'>{data.name} {data.model}</h1>
                                    <Card.Text className='text-center'>Dimensiones:  {data.size} </Card.Text>
                                
                                </div>

                </div>                        

                </div>

                </div>
            );
        
}

 export default ItemDetailContainer;
