import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import Button from 'react-bootstrap/Button';

import './ItemDetailContainer.css';

import Items from '../ItemListContainer/data.json';
import { Link, useParams } from 'react-router-dom';
import { useState , useEffect} from 'react'; 



        const ItemDetailContainer = () =>
        {


            const {id} = useParams();

            const [item, setitems] = useState([]);


            const pedirDatos = () =>
            {
                            return new Promise( (resuelta)=>
                            {
                                            resuelta(Items);            
                                    
                            })
            };

            useEffect(() => {
                pedirDatos()
                        .then((result) => {

                                if(id)
                                {
                                    setitems(result.filter(item  => item.id == id));
                                    
                                }
                                        
                        }).catch((err) => {
                                        console.log(err);
                        });
            },[id]);

                        console.log(Items)
            return(
                <div className='content'>
                       
                                       {
                                                       item.map((data=>
                                                       (
                                                        
                                                        <div className="item card " >
                                                            <div className='row col-md-12'>
                                        
                                                                    <div className='col-md-5'>
                                        
                                                                            <img  className='img-principal'  src={require(`../../img/media/${data.img}`)} ></img>

                                                                      
                                                                    </div>
                                        
                                                                    <div className='col-md-7'>

                                        
                                                                        <h1 className='text-center'>{data.name} {data.model}  </h1>
                                                                  
                                                                                
                                                                        <div className='row col-md-10 carrito '>

                                                                                        <div className='col-md-3'>
                                                                                                
                                                                                        <Form.Select aria-label="Default select pt-1">
                                                                                                
                                                                                                <option value="1">1</option>
                                                                                                <option value="2">2</option>
                                                                                                <option value="3">3</option>
                                                                                                <option value="4">4</option>
                                                                                        </Form.Select>

                                                                                        </div>
                                                                                        <div className="col-md-7">
                                                                                                <div id="buy-now" className="btn btn-lg buy-now-button btn-primary">Añadir al carrito</div>
                                                                                      </div>          
                                                                                
                                                                        </div>    

                                                                                <b className='btn btn-outline-primary'>Precio $ {data.price}</b><br></br>
                                                                                <Card.Text classNameName='text-left'><h4>Descripción<br></br>  {data.description} </h4> </Card.Text>
                                                                                <Card.Text className='text-left'><h4>Dimensiones <br></br> {data.size}</h4> </Card.Text>   
                                                                                <div id="regresar" className="btn btn-lg buy-now-button btn-primary">Regresar</div>
                                                                                        
                                                                </div>       

                                        
                                                        </div>                        
                                        
                                                        </div>
                                        
                                                       )))
                                                       
                                       }
                                               
               </div>
               
               );
        
}

 export default ItemDetailContainer;
