import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import '../user.css';
import { useState , useEffect} from 'react';   

const formulario = () =>
{

    const [email, setEmail] = useState(null);

    const handleEmail = (e)=>
    {
        setEmail(e.target.value);
    }


    const  handleEnviar=(e)=>
    {
        e.preventDefault();
     console.log(email)
    }

    return(

                <div className='card container formu'>
                         <Form onSubmit={handleEnviar}>
                            <h1>Iniciar sesión</h1>
                            <Form.Group className="mb-3" >
                            <Form.Label>Dirección de email</Form.Label>
                            <input type="email" placeholder="Enter correo" onChange={handleEmail} value={email} />
                            <Form.Text className="text-muted">
                               Ingrese por favor un correo electronico valido.
                            </Form.Text>
                            </Form.Group>
                    
                            <Form.Group className="mb-3" >
                            <Form.Label>Contraseña</Form.Label>
                            <Form.Control type="password" placeholder="Enter Contraseña" />
                            </Form.Group>
                            <Form.Group className="mb-3" >
                            <Form.Check type="checkbox" label="Recordarme" />
                            </Form.Group>
                            <Button variant="primary" type="submit">
                            Continuar
                            </Button>
                        </Form>
                        <hr></hr>
                        <h6 className="text-center">¿Eres nuevo?</h6>
                        <div className='btn btn-primary'>Crear Cuenta</div>
                </div>
            
    )
}

export default formulario;
