import './cart.css';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';


import { useContext } from 'react'; 

import { CartContext } from "../../context/cartContext";



const Cart = ()=>
{

        //valores globales de context para el cart section            
        const { cart,addCarrito,isInCart } = useContext(CartContext);

          console.log(cart)  

    return(
        <div className='content' >
              <div className="item card " >
              <h1 className='text-center'>CARRITO DE COMPRAS</h1>
                <div className=' mt-4 '>
                    <div className='row col-md-12 container'>
                        <div className='col-md-9'>
                                    {
                                            cart.map(data => (

                                                <div className='row col-md-12 ' key={data.id}><br></br>
                                                    <hr></hr>
                            
                                                        <div className='col-md-2'>
                            
                                                                <img  className='img-principal'  src={require(`../../img/media/${data.img}`)} ></img>
                                                        </div>
                            
                                                        <div className='col-md-8' >
                                                            <h4>{data.name} {data.model}  </h4>
                                                            <h4 className='text-primary'><b>{new Intl.NumberFormat('en-US', {style: 'currency', currency: 'USD'}).format(data.price)}</b></h4>
                                                                    
                                                                
                                                        </div>     
                                                        <div className='col-md-2'>
                                                        <InputGroup className="mb-3">
                                                                <Form.Control
                                                                placeholder={data.cantidad}
                                                                aria-label={data.cantidad}
                                                                aria-describedby={data.cantidad}
                                                                />
                                                                <InputGroup.Text id="basic-addon2">Cantidad</InputGroup.Text>
                                                                <InputGroup.Text id="basic-addon2">Cantidad</InputGroup.Text>
                                                            </InputGroup>
                                                        </div> 

                                                    
                            
                                            </div>


                                            ))
                                    }

                        </div>
                        <div className='col-md-3 card'>
                            <h5 className='mt-3 text-center'>RESUMEN DE TU PEDIDO</h5>

                        </div>
                    </div>
                </div> 
              </div>
        </div>
    )
}

export default Cart;
