import Card from 'react-bootstrap/Card';
import './ItemDetailContainer.css';

import Items from '../ItemListContainer/data.json';
import { Link, useParams } from 'react-router-dom';
import { useState , useEffect} from 'react'; 



        const ItemDetailContainer = () =>
        {


            const {id} = useParams();

            const [item, setitems] = useState([]);


            const pedirDatos = () =>
            {
                            return new Promise( (resuelta)=>
                            {
                                            resuelta(Items);            
                                    
                            })
            };

            useEffect(() => {
                pedirDatos()
                        .then((result) => {

                                if(id)
                                {
                                    setitems(result.filter(item  => item.id == id));
                                    
                                }
                                else
                                {
                                    setitems(result);
                                }

                                        
                        }).catch((err) => {
                                        console.log(err);
                        });
            },[id]);


            return(
                <div className="itemlist ">
                       
                       <h3 className="container">{greeting}</h3>
                               <div className='row col-md-12 productos'>
                                       
                                       {
                                                       item.map((data=>
                                                       (
                                                               <div className="col-lg-3 col-md-6 mb-4" key={data.id}>
                                                                       <Card  className="tarjetas" >
                                                                               <Card.Img variant="top"  src={require(`../../img/media/${data.img}`)}></Card.Img>
                                                                               <Card.Body>
                                                                               <Card.Title className='text-center'>{data.name} {data.model}</Card.Title>
                                                                               <h4  className='text-center text-primary'><b>$ {data.price}</b></h4>
                                                                               <Card.Text className='text-center'>Dimensiones:  {data.size} </Card.Text>
                                                                               </Card.Body>
                                                                               <Card.Footer className='d-grid gap-2'>
                                                                                               <Link className="btn btn-primary btn-"  to={`../item/${data.id}`}  size="md" > Ver detalles</Link>
                                                                               </Card.Footer>
                                                                       </Card>
                                                               </div> 
                                                       )))
                                                       
                                       }
                                               
               
                               </div>     
                </div>   
               
               );
        
}

 export default ItemDetailContainer;
