import Card from 'react-bootstrap/Card';
import './ItemDetailContainer.css';

import {Link} from 'react-router-dom'; 

const ItemDetailContainer = () =>
{
    const data = {
        "name":"Rines PHW",
        "model":"2350",
        "size":"Rines PHW",
    }

    return(
       <div className='content'>
        <div className="item card " >
            <div className='row col-md-12'>

                    <div className='col-md-6'>

                            <img  className='img-principal'  src={require(`../../img/media/1.png`)} ></img>

                    </div>

                    <div className='col-md-6'>

                    <h1 className='text-center'>{data.name} {data.model}</h1>
                    <h4  className='text-center text-primary'><b>$ {data.price}</b></h4>
                    <Card.Text className='text-center'>Dimensiones:  {data.size} </Card.Text>
                        
                        </div>

        </div>                        

        </div>

        </div>
    );
   
}

 export default ItemDetailContainer;
